import{_ as a,c as r,o as i,af as l}from"./chunks/framework.D1p3cIHr.js";const p=JSON.parse('{"title":"管理硬盘空间","description":"","frontmatter":{},"headers":[],"relativePath":"general-skills/computer-basic/SurfingTutorial/chap3/disk_management.md","filePath":"general-skills/computer-basic/SurfingTutorial/chap3/disk_management.md","lastUpdated":null}'),t={name:"general-skills/computer-basic/SurfingTutorial/chap3/disk_management.md"};function n(o,e,s,h,d,u){return i(),r("div",null,[...e[0]||(e[0]=[l('<h1 id="管理硬盘空间" tabindex="-1">管理硬盘空间 <a class="header-anchor" href="#管理硬盘空间" aria-label="Permalink to &quot;管理硬盘空间&quot;">​</a></h1><p>随着使用时间增长，硬盘空间会逐渐被占用。合理管理硬盘空间可以保持系统运行流畅，并且在项目维护时有更快的效率。</p><h2 id="日常使用的注意事项" tabindex="-1">日常使用的注意事项 <a class="header-anchor" href="#日常使用的注意事项" aria-label="Permalink to &quot;日常使用的注意事项&quot;">​</a></h2><h3 id="通用建议" tabindex="-1">通用建议 <a class="header-anchor" href="#通用建议" aria-label="Permalink to &quot;通用建议&quot;">​</a></h3><ul><li>安装第三方工具查看各文件夹占用硬盘的空间</li><li>备份重要文件后，及时清理不再使用的浏览器下载文件</li></ul><h3 id="windows-系统建议" tabindex="-1">Windows 系统建议 <a class="header-anchor" href="#windows-系统建议" aria-label="Permalink to &quot;Windows 系统建议&quot;">​</a></h3><p>部分建议适用于多分区的硬盘，有些 Windows 系统只有一个系统盘作为硬盘的分区</p><ul><li>安装系统后，将个人目录中的数个文件夹（文档、视频、图片、下载等）指向非系统盘</li><li>将微信的用户数据目录放到非系统盘</li></ul><h2 id="查看硬盘使用情况" tabindex="-1">查看硬盘使用情况 <a class="header-anchor" href="#查看硬盘使用情况" aria-label="Permalink to &quot;查看硬盘使用情况&quot;">​</a></h2><ul><li>Windows: 打开&quot;此电脑&quot;（部分版本被称为“我的电脑”），查看各磁盘的剩余空间</li><li>macOS: 关于本机 → 存储</li><li>Linux: 使用 <code>df -h</code> 命令</li></ul><h2 id="分析磁盘工具" tabindex="-1">分析磁盘工具 <a class="header-anchor" href="#分析磁盘工具" aria-label="Permalink to &quot;分析磁盘工具&quot;">​</a></h2><h3 id="filelight-多平台" tabindex="-1">Filelight (多平台) <a class="header-anchor" href="#filelight-多平台" aria-label="Permalink to &quot;Filelight (多平台)&quot;">​</a></h3><ul><li>详细信息：<a href="https://apps.kde.org/zh-cn/filelight/" target="_blank" rel="noreferrer">https://apps.kde.org/zh-cn/filelight/</a></li><li>开源可视化工具，使用环形图展示磁盘占用（GPL-2.0+ 协议）</li></ul><h3 id="windirstat-windows" tabindex="-1">WinDirStat (Windows) <a class="header-anchor" href="#windirstat-windows" aria-label="Permalink to &quot;WinDirStat (Windows)&quot;">​</a></h3><ul><li>详细信息：<a href="https://windirstat.net/" target="_blank" rel="noreferrer">https://windirstat.net/</a></li><li>经典的三窗格布局工具，支持文件类型着色显示（GPL 协议）</li></ul><h3 id="treesize-free-windows" tabindex="-1">TreeSize Free (Windows) <a class="header-anchor" href="#treesize-free-windows" aria-label="Permalink to &quot;TreeSize Free (Windows)&quot;">​</a></h3><ul><li>详细信息：<a href="https://www.jam-software.com/treesize_free" target="_blank" rel="noreferrer">https://www.jam-software.com/treesize_free</a></li><li>可快速扫描 NTFS 分区，支持右键菜单集成（免费版）</li></ul><h3 id="omnidisksweeper-macos" tabindex="-1">OmniDiskSweeper (macOS) <a class="header-anchor" href="#omnidisksweeper-macos" aria-label="Permalink to &quot;OmniDiskSweeper (macOS)&quot;">​</a></h3><ul><li>详细信息：<a href="https://www.omnigroup.com/more" target="_blank" rel="noreferrer">https://www.omnigroup.com/more</a></li><li>轻量级工具，支持按大小排序删除文件（免费）</li></ul><h3 id="grandperspective-macos" tabindex="-1">GrandPerspective (macOS) <a class="header-anchor" href="#grandperspective-macos" aria-label="Permalink to &quot;GrandPerspective (macOS)&quot;">​</a></h3><ul><li>详细信息：<a href="https://grandperspectiv.sourceforge.net/" target="_blank" rel="noreferrer">https://grandperspectiv.sourceforge.net/</a></li><li>使用矩形树状图可视化空间占用（GPL 协议）</li></ul><h3 id="ncdu-linux" tabindex="-1">ncdu (Linux) <a class="header-anchor" href="#ncdu-linux" aria-label="Permalink to &quot;ncdu (Linux)&quot;">​</a></h3><ul><li>详细信息：<a href="https://dev.yorhel.nl/ncdu" target="_blank" rel="noreferrer">https://dev.yorhel.nl/ncdu</a></li><li>终端交互式工具，支持远程 SSH 分析（MIT 协议）</li></ul><h3 id="baobab-linux" tabindex="-1">baobab (Linux) <a class="header-anchor" href="#baobab-linux" aria-label="Permalink to &quot;baobab (Linux)&quot;">​</a></h3><ul><li>详细信息：<a href="https://wiki.gnome.org/Apps/DiskUsageAnalyzer" target="_blank" rel="noreferrer">https://wiki.gnome.org/Apps/DiskUsageAnalyzer</a></li><li>GNOME 官方工具，提供环形图/树状图双视图（GPL 协议）</li></ul>',25)])])}const m=a(t,[["render",n]]);export{p as __pageData,m as default};
